import React from 'react'
import FeaturedStores from './FeaturedStores'

function CategoryRelatedStores(props) {
    const { categoryReduxStore } = useSelector((state) => state)
    const { categoryRedux } = categoryReduxStore;
    const category_title = categoryRedux.title;
    const hiffen_title = categoryRedux.hiffen_title;
    return (
        <section id="featured" className="featured-stores">
            <div className="container">
                <h1 className='title'>
                    {category_title}
                </h1>
                <hr className="subTitle-hr" />
                <FeaturedStores apiURL={`/api/${hiffen_title}/lojas-relacionadas`} />
            </div>
        </section>
    )
}

export default CategoryRelatedStores


export async function getServerSideProps({ params: { category } }) {

    return {
        props: { category_title: category }, // will be passed to the page component as props
    }
}
