export const userDataActionUpdate = (dataUpdate) => async (dispatch) => {
    dispatch({
        type: 'UPDATE',
        payload: dataUpdate
    });
}

export const userDataReducer = (state =
    {
        userData:
            { email: '', password: '' }
    }, action) => {
    switch (action.type) {
        case 'UPDATE':
            return {
                userData: {
                    ...state,
                    ...action.payload,
                }
            }
        default:
            return state;
    }
}
