import ConnectDB from '../../../assets/utils/ConnectDB'
import bcrypt from 'bcrypt'
import Users from '../../../assets/models/UserModel'
import { createAccessToken, createRefreshToken } from '../../../assets/utils/GenerateToken'


ConnectDB()

export default async (req, res) => {
    switch (req.method) {
        case 'POST':
            await login(req, res)
            break
    }
}

const login = async (req, res) => {
    try {

        const { email, password } = req.body

        const user = await Users.findOne({ email })
        if (!user) return res.status(400).json({ emailMessage: 'Esse email não está cadastrado' })

        const isMatch = await bcrypt.compare(password, user.password)
        if (!isMatch) return res.status(400).json({ passwordMessage: 'Senha incorreta' })

        /*const accessToken = createAccessToken({ id: user._id })
        const refreshToken = createRefreshToken({ id: user._id })

        res.json({
            msg: 'Login Success',
            refreshToken,
            accessToken,
            user: {
                name: user.name,
                email: user.email,
                role: user.role,
                avatar: user.avatar,
                admin: user.admin,
            }
        })*/

    } catch (err) {
        return res.status(500).json({ err: err.message })
    }
}
