import React, { useState } from 'react'
import validRegister from '../assets/utils/ValidRegister'
import Loading from '../snnipets/Loading'
import { useDispatch, useSelector } from 'react-redux'
import { loadingActionEnd, loadingActionStart } from '../redux_store/LoadingRegisterAndLogin';
import { postData } from '../assets/utils/fetchData';
import Link from 'next/link';
import { userDataActionUpdate } from '../redux_store/UserData';

function Register() {

    const dispatch = useDispatch();
    const { loading } = useSelector(state => state).loadingRegisterAndLoginReduxStore
    const { userData } = useSelector(state => state).userDataReducer
    const [submitStatus, setSubmitStatus] = useState({
        emailMessage: '',
        passwordMessage: '',
        emailAlreadyExist: '',
    })

    const { email, password } = userData

    const handleInputChange = e => {
        const { name, value } = e.target
        dispatch(userDataActionUpdate({ [name]: value }))
    }

    const handleSubmit = async e => {
        e.preventDefault()

        const { emailMessage, passwordMessage } = validRegister(userData.email, userData.password)

        setSubmitStatus({
            emailMessage: emailMessage,
            passwordMessage: passwordMessage,
        })

        if (emailMessage || passwordMessage) {
            return
        }

        dispatch(loadingActionStart())
        const data = await postData('/api/auth/register', userData);
        dispatch(loadingActionEnd())

        setSubmitStatus({
            emailAlreadyExist: data.emailMessage,
        })

    }

    return (
        <div className="container text-center p-4">
            <form className="d-block" onSubmit={handleSubmit} noValidate>
                <div className="form-group">
                    <label htmlFor="email" className="d-block">Email</label>
                    <input type="email" id="email"
                        //aria-describedby="emailHelp" 
                        name="email"
                        onChange={handleInputChange} value={email}
                    />
                    {
                        submitStatus.emailMessage ?
                            (
                                <p>{submitStatus.emailMessage}</p>
                            ) : submitStatus.emailAlreadyExist ?
                                (
                                    <p>
                                        {submitStatus.emailAlreadyExist}.
                                    Deseja fazer <Link href="/entrar"><a>login</a></Link>?
                                    </p>
                                ) : (
                                    ''
                                )
                    }
                </div>
                <div className="form-group">
                    <label htmlFor="password" className="d-block">Senha</label>
                    <input type="password" id="password" name="password"
                        onChange={handleInputChange} value={password} />
                    {
                        submitStatus.passwordMessage ?
                            (
                                <p>{submitStatus.passwordMessage}</p>
                            ) :
                            (
                                ''
                            )
                    }
                </div>
                <button type="submit">
                    {
                        loading ?
                            (
                                <>
                                    <Loading />
                                </>
                            ) : (
                                'CADASTRAR'
                            )
                    }
                </button>
            </form>
        </div>
    )
}

export default Register
