import React, { useState } from 'react'
import { postData } from '../assets/utils/fetchData'
import validRegister from '../assets/utils/ValidRegister'
import Loading from '../snnipets/Loading'

function Register() {
    const initialState = { email: '', password: '' }
    const [userData, setUserData] = useState(initialState)
    const [submitStatus, setSubmitStatus] = useState({
        emptyEmailToUse: false,
        invalidEmailToUse: false,
        emptyPasswordToUse: false,
        invalidPasswordToUse: false,
        existingEmail: false,
        submitIsOk: false,
        emailMessage: '',
        passwordMessage: '',
        emailIsOk: true,
        passwordIsOk: true,
    })

    const { email, password } = userData

    const handleInputChange = e => {
        const { name, value } = e.target
        setUserData({ ...userData, [name]: value })
    }

    const handleSubmit = async e => {
