import React, { useState } from 'react'
import validRegister from '../assets/utils/ValidRegister'

function Register() {
    const initialState = { email: '', password: '' }
    const [userData, setUserData] = useState(initialState)
    const [emptyEmailToUse, setEmptyEmail] = setState(false)
    const [invalidEmailToUse, setInvalidEmail] = setState(false)
    const [emptyPasswordToUse, setEmptyPassword] = setState(false)
    const [invalidPasswordToUse, setInvalidPassword] = setState(false)

    const { email, password } = userData

    const handleInputChange = e => {
        const { name, value } = e.target
        setUserData({ ...userData, [name]: value })
    }

    const handleSubmit = e => {
        e.preventDefault()
        const { emptyEmail, invalideEmail, emptyPassword, invalidPassword } = validRegister(userData.email, userData.password)
        setEmptyEmail(emptyEmail)
        setInvalidEmail(invalideEmail)
        setEmptyPassword(emptyPassword)
        setInvalidPassword(invalidPassword)
        console.log(userData)
    }

    return (
        <div className="container text-center p-4">
            <form className="d-block" onSubmit={handleSubmit}>
                <div className="form-group">
                    <label htmlFor="email" className="d-block">Email</label>
                    <input type="email" id="email" aria-describedby="emailHelp" name="email"
                        onChange={handleInputChange} value={email}
                    />
                    {
                        emptyEmailToUse ?
                            (
                                <p>Por favor, preencha seu e-mail</p>
                            ) : invalidEmailToUse ?
                                (
                                    <p>Por favor, preencha um e-mail válido</p>
                                ) :
                                (
                                    ''
                                )
                    }
                </div>
                <div className="form-group">
                    <label htmlFor="password" className="d-block">Senha</label>
                    <input type="password" id="password" name="password"
                        onChange={handleInputChange} value={password} />
                    {
                        emptyPasswordToUse ?
                            (
                                <p>Por favor, preencha seu e-mail</p>
                            ) : invalidPasswordToUse ?
                                (
                                    <p>Por favor, preencha um e-mail válido</p>
                                ) :
                                (
                                    ''
                                )
                    }
                </div>
                <button type="submit">
                    CADASTRAR
                </button>
            </form>
        </div>
    )
}

export default Register
