import React, { useEffect } from 'react';
import FeaturedStores from '../sections/FeaturedStores';
import GeneralFeaturedCoupons from '../sections/GeneralFeaturedCoupons';
import Head from 'next/head';
import ShowContentWithLoadingOrError from '../snnipets/ShowContentWithLoadingOrError';
import { featuredStoresAction } from '../redux_store/actions/FeaturedStoresActions';
import { couponsAction } from '../redux_store/actions/couponsActions';
import { useDispatch, useSelector } from 'react-redux'

export default function IndexPage() {
  const dispatch = useDispatch();
  const { featuredStores, coupons } = useSelector((state) => state)
  const { loading, error, ToUsefeaturedStores } = featuredStores;
  const { ToUseCoupons } = coupons
  useEffect(() => {
    dispatch(featuredStoresAction())
    dispatch(couponsAction())
  }, []);

  return (
    <>
      <Head>
        <title>Morant</title>
      </Head>
      <section id="featured" className="featured-stores">
        <div className="container">
          <h1 className="title">Cupons e cashback em 2.000 lojas</h1>
          <h2 className="subtitle">Economize na sua compra usando os melhores Cupons de Desconto e Cashback!</h2>
          <ShowContentWithLoadingOrError loading={loading} error={error}>
            <FeaturedStores featuredStores={ToUsefeaturedStores} />
          </ShowContentWithLoadingOrError>

        </div>
      </section >
      <ShowContentWithLoadingOrError loading={loading} error={error}>
        <GeneralFeaturedCoupons generalFeaturedCoupons={ToUseCoupons} />
      </ShowContentWithLoadingOrError>
    </>
  )
}
