import React, { useEffect, useState } from 'react';
import FeaturedStores from '../sections/FeaturedStores';
import GeneralFeaturedCoupons from '../sections/GeneralFeaturedCoupons';
import Head from 'next/head';
import data from '../../backend/data';
import axios from 'axios';

export default function IndexPage() {
  const [featuredStores, setFeaturedStores] = useState([]);
  const [generalFeaturedCoupons, setGeneralFeaturedCoupons] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(false);
  useEffect(() => {
    const fetchStoresData = async () => {
      try {
        setLoading(true);
        const { data } = await axios.get('http://localhost:5000/api/stores/index-featured-estores');
        setLoading(false);
        setFeaturedStores(data);
      } catch (err) {
        setError(err.message)
        setLoading(false)
      }
    }
    fetchStoresData();

    const fetchCouponsData = async () => {
      try {
        setLoading(true);
        const { data } = await axios.get('http://localhost:5000/api/coupons/general-featured-coupons');
        setLoading(false);
        setGeneralFeaturedCoupons(data);
      } catch (err) {
        setError(err.message)
        setLoading(false)
      }
    }
    fetchCouponsData();

  }, []);

  return (
    <>
      <Head>
        <title>Morant</title>
      </Head>
      <section id="featured" className="featured-stores">
        <div className="container">
          <h1 className="title">Cupons e cashback em 2.000 lojas</h1>
          <h2 className="subtitle">Economize na sua compra usando os melhores Cupons de Desconto e Cashback!</h2>

          <FeaturedStores featuredStores={featuredStores} />

        </div>
      </section >

      <GeneralFeaturedCoupons generalFeaturedCoupons={generalFeaturedCoupons} />
    </>
  )
}
