import { REGISTER_FAIL, REGISTER_REQUEST, REGISTER_SUCCESS } from "../Constants"

export const registerReducer = (state = {
    messages: { emailMessage: '', passwordMessage: '' },
    loading: false,
}, action) => {
    switch (action.type) {
        case REGISTER_REQUEST:
            return {
                loading: true,
                messages: { emailMessage: '', passwordMessage: '' },
            }
        case REGISTER_SUCCESS:
            return {
                loading: false,
                messages: action.payload
            }
        case REGISTER_FAIL:
            return {
                loading: false,
                messages: { emailMessage: '', passwordMessage: '' },
            }
        default:
            return state;
    }
}
