import ConnectDB from '../../../assets/utils/ConnectDB'
import validRegister from '../../../assets/utils/ValidRegister'
import bcrypt from 'bcrypt'
import Users from '../../../assets/models/UserModel'


ConnectDB()

export default async (req, res) => {
    switch (req.method) {
        case 'POST':
            await register(req, res)
            break
    }
}

const register = async (req, res) => {
    try {

        const { email, password } = req.body
        const isValid = validRegister(email, password)

        if (!isValid) return res.status(400).json({ err: isValid.msg })

        const user = await Users.findOne({ email })
        if (user) return res.status(400).json({ err: 'Esse email j치 est치 cadastrado' })

        const passwordHash = await bcrypt.hash(password, 12)

        const newUser = new Users({ email, password: passwordHash })

        console.log(newUser)

        await newUser.save()
        res.json({ msg: 'Register Success' })
    } catch (err) {
        return res.status(500).json({ err: err.message })
    }
}
