import { REGISTER_FAIL, REGISTER_REQUEST, REGISTER_SUCCESS } from "../Constants"

export const registerReducer = (state = {}, action) => {
    switch (action.type) {
        case REGISTER_REQUEST:
            return {
                loading: true,
            }
        case REGISTER_SUCCESS:
            return {
                loading: false,
                emailAlreadyExists: action.payload
            }
        case REGISTER_FAIL:
            return {
                loading: false,
            }
        default:
            return state;
    }
}
