import { COUPONS_FAIL, COUPONS_REQUEST, COUPONS_SUCCESS } from "../Constants";

export const categoryReducer = (state = { loading: true, category: {} }, action) => {
    switch (action.type) {
        case COUPONS_REQUEST:
            return { loading: true, category: {} }
        case COUPONS_SUCCESS:
            return { loading: false, category: action.payload }
        case COUPONS_FAIL:
            return { loading: false, error: action.payload, category: {} }
        default:
            return state;
    }
}
