import Axios from 'axios'
import { TOP_CATEGORIES_FAIL, TOP_CATEGORIES_REQUEST, TOP_CATEGORIES_SUCCESS } from '../constants/TopCategoriesConstant';

export const listTopCategoriesAction = () => async (dispatch) => {
    dispatch({
        type: TOP_CATEGORIES_REQUEST
    });
    try {
        const { data } = await Axios.get('http://localhost:5000/api/categories/top-categories');
        dispatch({ type: TOP_CATEGORIES_SUCCESS, payload: data })
    } catch (error) {
        dispatch({ type: TOP_CATEGORIES_FAIL, payload: error.message })
    }
}
