import { REGISTER_FAIL, REGISTER_REQUEST, REGISTER_SUCCESS } from "../Constants"
import { postData } from "../../assets/utils/fetchData";

export const registerAction = (apiURL, post) => async (dispatch) => {
    dispatch({
        type: REGISTER_REQUEST,
    });
    try {
        const data = await postData(apiURL, post);
        dispatch({ type: REGISTER_SUCCESS, payload: data.emailMessage })
    } catch (error) {
        dispatch({ type: REGISTER_FAIL, payload: error.message })
    }
}
