import React, { useState } from 'react'
import Loading from '../snnipets/Loading'
import { useDispatch, useSelector } from 'react-redux'
import validLogin from '../assets/utils/ValidLogin';
import { loadingActionEnd, loadingActionStart } from '../redux_store/LoadingRegisterAndLogin';
import { postData } from '../assets/utils/fetchData';
import Link from 'next/link'

function Register() {

    const dispatch = useDispatch();
    const { loading } = useSelector((state) => state).loadingRegisterAndLoginReduxStore

    const initialState = { email: '', password: '' }
    const [userData, setUserData] = useState(initialState)
    const [submitStatus, setSubmitStatus] = useState({
        emailMessage: '',
        passwordMessage: '',
    })

    const { email, password } = userData

    const handleInputChange = e => {
        const { name, value } = e.target
        setUserData({ ...userData, [name]: value })
    }

    const handleSubmit = async e => {
        e.preventDefault()

        const { emailMessage, passwordMessage } = validLogin(userData.email, userData.password)

        setSubmitStatus({
            emailMessage: emailMessage,
            passwordMessage: passwordMessage,
        })

        if (emailMessage || passwordMessage) {
            return
        }

        dispatch(loadingActionStart())

        const data = await postData('/api/auth/login', userData);

        dispatch(loadingActionEnd())

        setSubmitStatus({
            emailNotExist: data.emailMessage,
            wrongPassword: data.passwordMessage,
        })

        if (data.emailMessage || data.passwordMessage)

            console.log()
    }

    return (
        <div className="container text-center p-4">
            <form className="d-block" onSubmit={handleSubmit} noValidate>
                <div className="form-group">
                    <label htmlFor="email" className="d-block">Email</label>
                    <input type="email" id="email"
                        //aria-describedby="emailHelp" 
                        name="email"
                        onChange={handleInputChange} value={email}
                    />
                    {
                        submitStatus.emailMessage ?
                            (
                                <p>{submitStatus.emailMessage}</p>
                            ) : submitStatus.emailNotExist ?
                                (
                                    <p>
                                        {submitStatus.emailNotExist}.
                                        Deseja se <Link href="/registrar"><a>cadastrar</a></Link>?
                                    </p>
                                ) : (
                                    ''
                                )
                    }
                </div>
                <div className="form-group">
                    <label htmlFor="password" className="d-block">Senha</label>
                    <input type="password" id="password" name="password"
                        onChange={handleInputChange} value={password} />
                    {
                        submitStatus.passwordMessage ?
                            (
                                <p>{submitStatus.passwordMessage}</p>
                            ) : submitStatus.wrongPassword ?
                                (
                                    <p>{submitStatus.wrongPassword}</p>
                                ) :
                                (
                                    ''
                                )
                    }
                </div>
                <button type="submit">
                    {
                        loading ?
                            (
                                <>
                                    <Loading />
                                </>
                            ) : (
                                'ENTRAR'
                            )
                    }
                </button>
            </form>
        </div>
    )
}

export default Register
