import React from 'react'
import FeaturedStores from './FeaturedStores'

function CategoryRelatedStores(props) {
    //const category = props.match.params.category;
    console.log(category)
    return (
        <section id="featured" className="featured-stores">
            <div className="container">
                <h1 className='title'>
                    {category.title}
                </h1>
                <hr className="subTitle-hr" />
                <FeaturedStores apiURL={`/api/${category}/lojas-relacionadas`} />
            </div>
        </section>
    )
}

export default CategoryRelatedStores

export async function getServerSideProps({ params: { category } }) {

    //const res = await getData(`categorias/${category}`);
    const res = categoryData;

    return {
        props: { category: res.category }, // will be passed to the page component as props
    }
}
