import { applyMiddleware, combineReducers, compose, createStore } from 'redux';
import thunk from 'redux-thunk';
import { listFeaturedStoreReducer } from "./reducers/FeaturedStoresReducer";
import { listCouponsReducer } from './reducers/CouponsReducer'
import { listTopCategoriesReducer } from './reducers/TopCategoriesReducer';
import { categoryReducer } from './reducers/CategoryReducer';

const initialState = {};
const reducer = combineReducers({
    listFeaturedStore: listFeaturedStoreReducer,
    listCoupons: listCouponsReducer,
    listTopCategories: listTopCategoriesReducer,
    categoryStore: categoryReducer
})

const defineComposeEnhancer = () => {
    if (typeof window !== 'undefined') {
        return window.__REDUX_DEVTOOLS_EXTENSION_COMPOSE__ || compose;
    } else {
        return compose
    }
}
const composeEnhancer = defineComposeEnhancer();
const Store = createStore(reducer, initialState, composeEnhancer(applyMiddleware(thunk)));

export default Store;
