import ACTION from './Constants'
import { getData } from "../assets/utils/fetchData";
import { DataContext } from '../store/GlobalState'
import { useContext } from 'react'

const baseURL = process.env.BASE_URL
const [state, dispatch] = useContext(DataContext);


//CATEGORY ========================================================================
export const categoryAction = (category_hiffen_name) => async (dispatch) => {
    dispatch({
        type: ACTION.CATEGORY_TITLE_REQUEST, payload: category_hiffen_name
    });
    try {
        const data = await getData(category_hiffen_name);
        dispatch({ type: ACTION.CATEGORY_TITLE_SUCCESS, payload: data })
    } catch (error) {
        dispatch({
            type: ACTION.CATEGORY_TITLE_FAIL,
            payload:
                error.response && error.response.data.message ?
                    error.response.data.message : error.message
        })
    }
}

//COUPONS ========================================================================
export const listCouponsAction = (apiURL) => async (dispatch) => {
    dispatch({
        type: ACTION.COUPONS_REQUEST
    });
    try {
        const data = await getData(apiURL);
        dispatch({ type: ACTION.COUPONS_SUCCESS, payload: data })
    } catch (error) {
        dispatch({ type: ACTION.COUPONS_FAIL, payload: error.message })
    }
}

//FEATURED STORES ========================================================================
export const listFeaturedStoresAction = (apiURL) => async (dispatch) => {
    dispatch({
        type: ACTION.FEATURED_STORE_REQUEST
    });
    try {
        const data = await getData(apiURL);
        dispatch({ type: ACTION.FEATURED_STORE_SUCCESS, payload: data })
    } catch (error) {
        dispatch({ type: ACTION.FEATURED_STORE_FAIL, payload: error.message })
    }
}

//Top Categories ========================================================================
export const listTopCategoriesAction = (apiURL) => async (dispatch) => {
    dispatch({
        type: ACTION.TOP_CATEGORIES_REQUEST
    });
    try {
        const data = await getData(apiURL);
        dispatch({ type: ACTION.TOP_CATEGORIES_SUCCESS, payload: data })
    } catch (error) {
        dispatch({ type: ACTION.TOP_CATEGORIES_FAIL, payload: error.message })
    }
}

//loading Register And Login ========================================================================
export const loadingActionStart = () => async (dispatch) => {
    dispatch({
        type: ACTION.START,
    });
}
export const loadingActionEnd = () => async (dispatch) => {
    dispatch({
        type: ACTION.END,
    });
}

