import React, { useState } from 'react'
import { postData } from '../assets/utils/fetchData'
import validRegister from '../assets/utils/ValidRegister'
import Loading from '../snnipets/Loading'

function Register() {
    const initialState = { email: '', password: '' }
    const [userData, setUserData] = useState(initialState)
    const [submitStatus, setSubmitStatus] = useState({
        emptyEmailToUse: false,
        invalidEmailToUse: false,
        emptyPasswordToUse: false,
        invalidPasswordToUse: false,
        setSubmitiIsOk: false,
    })
    const [emptyEmailToUse, setEmptyEmail] = useState(false)
    const [invalidEmailToUse, setInvalidEmail] = useState(false)
    const [emptyPasswordToUse, setEmptyPassword] = useState(false)
    const [invalidPasswordToUse, setInvalidPassword] = useState(false)
    const [submitIsOk, setSubmitiIsOk] = useState(false)

    const { email, password } = userData

    const handleInputChange = e => {
        const { name, value } = e.target
        setUserData({ ...userData, [name]: value })
    }

    const handleSubmit = async e => {
        e.preventDefault()
        const { emptyEmail, invalidEmail, emptyPassword, invalidPassword, msg } = validRegister(userData.email, userData.password)
        setSubmitStatus({
            emptyEmailToUse: emptyEmail,
            invalidEmailToUse: invalidEmail,
            emptyPasswordToUse: emptyPassword,
            invalidPasswordToUse: emptyPassword,
        })
        setEmptyEmail(emptyEmail)
        setInvalidEmail(invalidEmail)
        setEmptyPassword(emptyPassword)
        setInvalidPassword(invalidPassword)

        if (msg) {
            console.log(msg)
            return
        }
        //console.log(userData)
        const res = await postData('/api/auth/register', userData)
        setSubmitStatus({
            setSubmitiIsOk: true,
        })
    }

    return (
        <div className="container text-center p-4">
            <form className="d-block" onSubmit={handleSubmit} noValidate>
                <div className="form-group">
                    <label htmlFor="email" className="d-block">Email</label>
                    <input type="email" id="email"
                        //aria-describedby="emailHelp" 
                        name="email"
                        onChange={handleInputChange} value={email}
                    />
                    {
                        emptyEmailToUse ?
                            (
                                <p>Por favor, preencha seu e-mail</p>
                            ) : invalidEmailToUse ?
                                (
                                    <p>Por favor, preencha um e-mail v√°lido</p>
                                ) :
                                (
                                    ''
                                )
                    }
                </div>
                <div className="form-group">
                    <label htmlFor="password" className="d-block">Senha</label>
                    <input type="password" id="password" name="password"
                        onChange={handleInputChange} value={password} />
                    {
                        emptyPasswordToUse ?
                            (
                                <p>Por favor, defina uma senha</p>
                            ) : invalidPasswordToUse ?
                                (
                                    <p>A senha deve conter pelo menos 6 caracteres</p>
                                ) :
                                (
                                    ''
                                )
                    }
                </div>
                <button type="submit">
                    {
                        submitIsOk ?
                            (
                                <>
                                    <Loading />
                                </>
                            ) : (
                                'Entrar'
                            )
                    }
                </button>
            </form>
        </div>
    )
}

export default Register
