import { FAIL, REQUEST, SUCCESS } from "../Constants"
import { postData } from "../../assets/utils/fetchData";

export const loginAction = (apiURL, post) => async (dispatch) => {
    dispatch({
        type: REQUEST,
    });
    try {
        const data = await postData(apiURL, post);
        dispatch({ type: SUCCESS, payload: data.emailMessage })
    } catch (error) {
        dispatch({ type: FAIL, payload: error.message })
    }
}
