import { COUPONS_FAIL, COUPONS_REQUEST, COUPONS_SUCCESS } from "../constants/CouponsConstants";

export const couponsReducer = (state = { featuredStores: [] }, action) => {
    switch (action.type) {
        case COUPONS_REQUEST:
            return { loading: true }
        case COUPONS_SUCCESS:
            return { loading: false, featuredStores: action.payload }
        case COUPONS_FAIL:
            return { loading: false, error: action.payload }
        default:
            return state;
    }
}
