import { FAIL, REQUEST, SUCCESS } from "../Constants"

export const loginReducer = (state = {}, action) => {
    switch (action.type) {
        case REQUEST:
            return {
                loading: true,
            }
        case SUCCESS:
            return {
                loading: false,
                emailNotExist: action.payload.emailMessage,
                wrongPassword: action.payload.passwordMessage,
            }
        case FAIL:
            return {
                loading: false,
            }
        default:
            return state;
    }
}
